import { Box, ThemeProvider } from "@mui/system";

export default function Message(props) {
    return (
        <ThemeProvider
      theme={{
        palette: {
          primary: {
            main: '#007FFF',
            dark: '#0059B2',
          }
        },
      }}
    >
      <Box
        sx={{
          width: 300,
          height: 300,
          bgcolor: 'primary.main',
          '&:hover': {
            backgroundColor: 'primary.dark',
            opacity: [0.9, 0.8, 0.7],
          },
        }}
      >
        {props.message.content}
      </Box>
    </ThemeProvider>
    )
}
